CREATE TABLE `visitors` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT, `user_ip` VARCHAR(20) CHARSET ASCII NOT NULL, `test_count` INT NOT NULL DEFAULT 0, `test_email` VARCHAR(100) CHARSET ASCII, `created_at` DATETIME DEFAULT NOW(), `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`) ) CHARSET=utf8; 
CREATE TABLE `transactions` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT, `user_id` INT UNSIGNED NOT NULL, `email_id` INT NOT NULL, `price_type` INT NOT NULL DEFAULT 1, `price` FLOAT NOT NULL, `qty` INT NOT NULL, `mode` VARCHAR(20) CHARSET utf8, `authority` VARCHAR(200) CHARSET utf8, `bank` VARCHAR(20) CHARSET utf8, `type` VARCHAR(10) CHARSET utf8, `income` FLOAT NOT NULL DEFAULT 0, `gift` VARCHAR(150) CHARSET utf8, `balance_id` INT NOT NULL DEFAULT 0, `remain_time` TEXT CHARSET utf8, `created_at` DATETIME DEFAULT NOW(), `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`) ) CHARSET=utf8; 
CREATE TABLE `balances` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT, `charge_date` DATETIME NOT NULL DEFAULT NOW(), `user_id` INT UNSIGNED NOT NULL, `email_id` INT UNSIGNED NOT NULL, `price_type` INT UNSIGNED NOT NULL, `price` FLOAT UNSIGNED NOT NULL, `qty` FLOAT UNSIGNED NOT NULL, `supply` INT UNSIGNED NOT NULL, `used` INT UNSIGNED NOT NULL DEFAULT 0, `ending_date` DATETIME, `created_at` DATETIME DEFAULT NOW(), `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`) ) CHARSET=utf8; 
ALTER TABLE  `visitors` ADD COLUMN `mail_id` INT NOT NULL AFTER `test_email`; 